import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    let data = [
      {
        pseudo: "Moussa",
        age: 30,
        techno: ["Javascript", " nodeJs", "Pythion"],
        admin: true
      },
      {
        pseudo: "Yass",
        age: 18,
        techno: ["Php", " nodeJs", "Pythion"],
        admin: false
      },
      {
        pseudo: "Nicola",
        age: 70,
        techno: ["Ruby", " nodeJs", "Pythion"],
        admin: true
      },
      {
        pseudo: "Isaac",
        age: 17,
        techno: ["C++", " Java", "Pythion"],
        admin: false
      }
    ];

    document.body.addEventListener("click", e => {
      switch (e.target.id) {
        case "javascript":
          document.body.style.background = "yellow";

          break;
        case "php":
          document.body.style.background = "blue";

          break;
        case "nodeJs":
          document.body.style.background = "green";

          break;
        case "Python":
          document.body.style.background = "purple";

          break;

        default:
          document.body.style.background = "black";

          break;
      }
    });
  });
  return (
    <>
      <h3 id="javascript">Javascript</h3>
      <h3 id="php">php</h3>
      <h3 id="nodeJs">nODEJS</h3>
      <h3 id="Python">python</h3>
    </>
  );
};

export default App;
