import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const btn1 = document.querySelector(".btn1");
    const btn2 = document.querySelector(".btn2");
    const result = document.querySelector("p");
    const firstBox = document.querySelector(".firstBox");
    const input = document.querySelector('input[type="text"]');

    console.log(input);

    input.addEventListener("input", e => {
      let key = document.querySelector(".key");
      key += e.target.value;
      console.log(e.target.value);
    });
  });
  return (
    <>
      <div className="container">
        <div className="firstBox box">
          <h1>Capital de la France</h1>
          <button className="btn1 mx-2 btn btn-light text-success">
            Marseille
          </button>
          <button className="btn2 btn btn-light text-success">Paris</button>
          <p className="text-light text-center">La capitale est Paris</p>
        </div>

        <div className="box">
          <form action="" method="get">
            <h1>Quelle est ton language préféré</h1>
            <input type="text" placeholder="écrit" />
            <h2>
              QUEL LETTRE <span className="key"></span>
            </h2>
            <select>
              <option value="nodeJs">NodeJs</option>
              <option value="Python">Python</option>
              <option value="javascript">Javascript</option>
            </select>
          </form>
        </div>
      </div>
      <div className="cube"></div>
    </>
  );
};

export default App;
