          7
        </button>
        <button className="btn" id="6">
          6
        </button>
        <button className="btn" id="5">
          5
        </button>
        <button className="btn" id="4">
          4
        </button>
        <button className="btn" id="3">
          3
        </button>
        <button className="btn" id="2">
          2
        </button>
        <button className="btn" id="1">
          1
        </button>
        <button className="btn" id=".">
          .
        </button>
        <button className="btn" id="+">
          +
        </button>
        <button className="btn" id="-">
          -
        </button>{" "}
        <button className="btn" id="*">
          *
        </button>{" "}
        <button className="btn" id="/">
          /
        </button>
        <button className="btn" id="clear">
          C
        </button>{" "}
        <button className="btn" id="equal">
          =
        </button>{" "}
        <button className="btn" id="/">
          /
        </button>
      </div>
    </>
  );
};

export default App;
