import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const target = document.getElementById("target");
    let array = ["Développeur", "Infographiste", "Préparateur de commande"];
    let wordIndex = 0;
    let letterIndex = 0;

    const createLetter = () => {
      const letter = document.createElement("span");
      target.appendChild(letter);

      letter.textContent = array[wordIndex][letterIndex];
    };
    createLetter();

    const loop = () => {
      setTimeout(() => {
        if (letterIndex < array[wordIndex].length) {
          createLetter();
          letterIndex++;
          loop();
        }
      }, 50);
    };
    loop();
  });
  return (
    <>
      <h3>Je suis un</h3>
      <h3 id="target"></h3>
    </>
  );
};

export default App;
