import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const target = document.getElementById("target");
    let array = ["Développeur", "Infographiste", "Préparateur de commande"];
    let wordIndex = 0;
    let letterIndex = 0;

    const createLetter = () => {
      const letter = document.createElement("span");
      target.appendChild(letter);

      letter.textContent = array[wordIndex][letterIndex];
      setTimeout(() => {
        letter.remove();
      }, 2800);
    };

    const loop = () => {
      setTimeout(() => {
        if (wordIndex >= array.length) {
          wordIndex = 0;
          letterIndex = 0;
          loop();
        }
        if (letterIndex < array[wordIndex].length) {
          createLetter();
          letterIndex++;
          loop();
        } else {
          wordIndex++;
          letterIndex = 0;
          setTimeout(() => {
            loop();
          }, 2800);
        }
      }, 50);
    };
    loop();
  });
  return (
    <>
      <h3>
        Je suis un <span id="target"></span>
      </h3>
    </>
  );
};

export default App;
