import React, { useEffect, useState } from "react";
import { parse } from "qs";

const App = () => {
  useEffect(() => {
    const target = document.querySelector("#target");

    let earnhisLife = ["Developpeur", "Photographe", "Infographiste"];
    let wordIndex = 0;
    let letterIndex = 0;
    console.log(earnhisLife[wordIndex].length);

    const numberOne = () => {
      const spanOne = document.createElement("span");
      target.appendChild(spanOne);

      spanOne.textContent = earnhisLife[wordIndex][letterIndex];
      // setTimeout(() => {
      //   spanOne.remove();
      // }, 1000);
    };

    const loop = () => {
      setTimeout(() => {
        if (letterIndex < earnhisLife[wordIndex].length) {
          numberOne();
          letterIndex++;
          loop();
        } else {
          wordIndex++;
          letterIndex = 0;

          numberOne();

          loop();
        }
      }, 50);
    };
    loop();
  });

  return (
    <>
      <h1 className="mt-5 mx-auto text-center d-flex justify-content-center">
        Je suis <span id="target"> </span>
      </h1>
    </>
  );
};

export default App;
