import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const questionContainer = document.querySelector(".click-event");
    const mousemove = document.querySelector(".mousemove");

    const keypressContainer = document.querySelector(".keypress");
    const key = document.querySelector("#key");

    document.addEventListener("keypress", e => {
      switch (e.key) {
        case "j":
          questionContainer.style.background = "purple";
          break;
        case "a":
          questionContainer.style.background = "green";
          break;
        case "v":
          questionContainer.style.background = "yellow";
          break;
        default:
          alert("no attribute");
      }
      ring();
    });
    const ring = () => {
      const audio = new Audio();
      audio.src = "./public/lazzio-tranzzak-clip-officiel.mp3";
      audio.play();
    };
  });

  return (
    <div>
      <div className="click-event box">
        <h4>Quelle est la capitale de la Suisses</h4>

        <button id="btn-1">Berne</button>
        <button id="btn-2">Zurich</button>
        <p>La bonne r√©ponse est Berne</p>
      </div>
      <div className="mousemove"></div>
      <div className="keypress box">
        <h2>
          Vous appuyez sur la touche: <span id="key"></span>
        </h2>
      </div>
    </div>
  );
};

export default App;
