import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const questionContainer = document.querySelector(".click-event");

    document.addEventListener("keypress", e => {
      switch (e.key) {
        case "j":
          questionContainer.style.background = "purple";
          break;
        case "a":
          questionContainer.style.background = "green";
          break;
        case "v":
          questionContainer.style.background = "yellow";
          break;
        default:
          alert("no attribute");
      }
      ring();
    });
    let audio = new Audio();
    const ring = () => {
      audio.src = "./lazzio-tranzzak-clip-officiel.mp3";
      audio.play();
    };

    document.addEventListener("keypress", e => {
      if (e.key === "g") {
        ring();
      } else if (e.key === "p") {
        audio.pause();
      }
    });
  });

  return (
    <>
      <nav className="bg-dark">
        <ul>
          <li>Acceuil</li>
          <li>Service</li>
          <li>Contact</li>
        </ul>
      </nav>
    </>
  );
};

export default App;
