import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const questionContainer = document.querySelector(".click-event");
    const mousemove = document.querySelector(".mousemove");
    console.log(mousemove);

    window.addEventListener("mousemove", e => {
      mousemove.style.left = e.pageX + "px";
      mousemove.style.top = e.pageY + "px";
    });

    window.addEventListener("mousedown", e => {
      mousemove.style.transform = "scale(2) translate(-25%, - 50%)";
    });

    questionContainer.addEventListener("mouseenter", () => {
      questionContainer.style.background = "rgba(0,0,0,0.6)";
    });
    questionContainer.addEventListener("mouseout", e => {
      console.log(e);
    });
  });

  return (
    <div>
      <div className="click-event box">
        <h4>Quelle est la capitale de la Suisses</h4>

        <button id="btn-1">Berne</button>
        <button id="btn-2">Zurich</button>
        <p>La bonne r√©ponse est Berne</p>
      </div>
      <div className="mousemove"></div>
    </div>
  );
};

export default App;
