import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const boxes = document.querySelectorAll(".box");
    console.log(boxes.length);
    boxes.forEach(box => {
      box.addEventListener("click", e => {
        e.target.style.transform = "scale(0.7)";
      });
    });
  });

  return <></>;
};

export default App;
