  useEffect(() => {
    const date = new Date();
    console.log(date);

    console.log(date.toISOString(date));

    const dateLocale = chaine => {
      let ok = new Date(chaine).toLocaleDateString("fr-FR", {
        day: "numeric",
        month: "long",
        year: "numeric",
        hour: "numeric",
        minute: "numeric"
      });
      return ok;
    };

    console.log(dateLocale(date));
  });

  return <></>;
};

export default App;
