import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const passwordGenerator = document.querySelector(".passwordGenerator");
    const boxBlack = document.querySelectorAll(".boxBlack");
    console.log(boxBlack);
    const uppercase = "AZERTYUIOPQSDFGHJKLMWXCVBN";

    boxBlack.forEach(myBox => {
      myBox.addEventListener("input", e => {
        let data = [];
        console.log(e.target.checked);

        // if (e.target.id === "uppercase") data.push(...uppercase);
      });
    });
    let stringPass = "Password Generator";
    passwordGenerator.textContent = stringPass.toLocaleUpperCase();
    var string1 = "okok";
    string1.toLocaleUpperCase();
  });

  return (
    <>
      <div className="myBox position-absolute top-50 start-50 translate-middle ">
        <h1 className="passwordGenerator"></h1>
        <input
          type="text"
          value="10"
          style={{}}
          className="inputDisplay fw-bolder display-6 text-light"
          readOnly
        />
        <div>
          <input type="range" className="form-range w-50" id="customRange1" />
          <label htmlFor="customRange1" className="form-label fw-bolder">
            10
          </label>
        </div>
        <div className="choose text-uppercase fw-bolder">
          <div className="boxBlack my-2 bg-dark">
            <label htmlFor="uppercase" className="fw-bolder">
              Majuscule
            </label>
            <input type="checkbox" name="" id="uppercase"></input>
          </div>
          <div className="boxBlack my-2 bg-dark">
            <label htmlFor="lowercase" className="fw-bolder">
              Minuscule
            </label>
            <input
              type="checkbox"
              name=""
              id="lowercase"
              className="fw-bolder"
            ></input>
          </div>
          <div className="boxBlack my-2 bg-dark">
            <label htmlFor="symbols" className="fw-bolder">
              Symbols
            </label>
            <input type="checkbox" name="" id="symbols"></input>
          </div>
          <div className="boxBlack my-2 bg-dark">
            <label htmlFor="number" className="fw-bolder">
              Majuscule
            </label>
            <input type="checkbox" name="" id="number"></input>
          </div>
        </div>

        <button
          type="button"
          className="btn btn-danger w-25 text-center fw-bolder "
        >
          Generate Password
        </button>
      </div>
    </>
  );
};

export default App;
