import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const btn1 = document.querySelector(".btn1");
    const btn2 = document.querySelector(".btn2");
    const result = document.querySelector("p");
    const firstBox = document.querySelector(".firstBox");
    firstBox.addEventListener("mouseenter", () => {
      firstBox.style.background = "green";
    });
    firstBox.addEventListener("mouseout", () => {
      firstBox.style.background = "purple";
    });

    btn1.addEventListener("click", e => {
      result.style.visibility = "visible";
      result.textContent = "False putain";
      result.style.background = "red";
    });
    btn2.addEventListener("click", e => {
      result.style.visibility = "visible";
      result.textContent = "La capitale est Paris";
      result.style.background = "green";
      playOk();
    });

    const playOk = () => {
      let audio = new Audio();
      audio.src = "./lazzio-tranzzak-clip-officiel.mp3";
      audio.play();
    };
    const select = document.querySelector("select");
    console.log(select);
  });
  return (
    <>
      <div className="container">
        <div className="firstBox box">
          <h1>Capital de la France</h1>
          <button className="btn1 mx-2 btn btn-light text-success">
            Marseille
          </button>
          <button className="btn2 btn btn-light text-success">Paris</button>
          <p className="text-light text-center">La capitale est Paris</p>
        </div>

        <div className="box">
          <form action="" method="get">
            <h1>Quelle est ton language préféré</h1>
            <select>
              <option value="nodeJs">NodeJs</option>
              <option value="Python">Python</option>
              <option value="javascript">Javascript</option>
            </select>
          </form>
        </div>
      </div>
      <div className="cube"></div>
    </>
  );
};

export default App;
