import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    //Form Events
    const inputName = document.querySelector('input[type="text"]');
    const select = document.querySelector("select");
    const cgv = document.querySelector("#cgv");
    const form = document.querySelector("form");
    let language = "";
    let pseudo = "";

    inputName.addEventListener("input", e => {
      language.innerHTML += e.target.value;
      console.log(e.target.value);
    });

    form.addEventListener("submit", e => {
      console.log("yes");

      if (cgv.checked) {
        document.querySelector("form > div").innerHTML = `
       <h3>Pseudo: ${pseudo} </h3>
       <h4>Language préféré: ${language}</h4>
       `;
      } else {
        console.log(cgv.checked);
      }
    });
  });

  return (
    <>
      <div>
        <div className="click-event box">
          <h4>Quelle est la capitale de la Suisse</h4>

          <button id="btn-1">Berne</button>
          <button id="btn-2">Zurich</button>
          <p>La bonne réponse est Berne</p>
        </div>
        <div className="mousemove"></div>
        <div className="keypress box">
          <h2>
            Vous appuyez sur la touche: <span id="key"></span>
          </h2>
        </div>
      </div>
      <form action="" className="box">
        <input type="text" placeholder="Entrez votre nom" />
        <select>
          <option value="">Séléctionner votre language favoris</option>
          <option value="PHP">PHP</option>
          <option value="python">python</option>
        </select>
        <input type="checkbox" name="" id="cgv" />
        <label id="cgv">Accepter les CGV</label>
        <input type="submit" value="Valider" />
        <div></div>
      </form>
    </>
  );
};

export default App;
