import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const target = document.getElementById("target");
    let array = ["Developpeur", "Infographiste", "Préparateur de commande"];
    let wordIndex = 0;
    let letterIndex = 0;

    const createLetter = () => {
      const letter = document.createElement("span");
      target.appendChild(letter);

      letter.textContent = array[wordIndex][letterIndex];
    };
    createLetter();

    setTimeout(() => {
      letterIndex++;
      createLetter();
    }, 200);

    for (let i = 0; i < 3; i++) {
      (() => {
        let reponse = prompt(
          "Hello hte world je veux etrez le meilleur en javascript"
        );
        let boy = "Moussa";
        if (reponse === "ok") {
          document.body.innerHTML = "<h2>OKOKOK</h2>";
        } else {
          document.body.innerHTML = `<h1>FUCK OFF</h1>`;
        }
      })();
      document.body.innerHTML = -i;
    }
  });
  return (
    <>
      <h3>Je suis un</h3>
      <h3 id="target"></h3>
    </>
  );
};

export default App;
