import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const btn1 = document.querySelector(".btn1");
    const btn2 = document.querySelector(".btn2");
    const btn = document.querySelectorAll(".btn");
    const result = document.querySelector("p");

    btn.forEach(button => {
      button.addEventListener("click", () => {
        if (btn[0]) {
          result.textContent = "False";
          result.style.background = "red";
        }
        console.log(button);
      });
    });
    console.log(btn);
  });
  return (
    <>
      <div className="container">
        <div className="box">
          <h1>Capital de la France</h1>
          <button className="btn1 mx-2 btn btn-light text-success">
            Marseille
          </button>
          <button className="btn2 btn btn-light text-success">Paris</button>
          <p className="text-light text-center">La capitale est Paris</p>
        </div>
      </div>
    </>
  );
};

export default App;
