import React, { useEffect } from "react";

const App = () => {
  useEffect(() => {
    const target = document.getElementById("target");
    let array = ["Developpeur", "Infographiste", "Préparateur de commande"];

    const createLetter = () => {
      const letter = document.createElement("span");
      target.appendChild(letter);

      letter.textContent = array[0][0];
    };
    createLetter();

    setInterval(createLetter, 200);
  });
  return (
    <>
      <h3>Je suis un</h3>
      <h3 id="target"></h3>
    </>
  );
};

export default App;
